        $display("%t | Test Case | A       | B       | Cin | Sum     | Cout | Overflow", $time);

        // Test case 1: Add two positive numbers within range
        #10;
        a = 16'd100;
        b = 16'd200;
        cin = 1'b0;
        #10;
        $display("%t | %d         | %d      | %d     | %d   | %d      | %d   | %d", $time, test_case++, a, b, cin, sum, cout, overflow);

