    $display("Running boundary_test at time %0t", $time);
    boundary_test();

    // Finish simulation
    $display("Testbench completed at time %0t", $time);
    $finish;
  end

  // Monitor for signal changes
  initial begin
    $monitor("Time %0t: a=%b, b=%b, sum=%b", $time, a, b, sum);
